// Schedule follow-up email for 3 days
const followUpDate = DateTime.now().plus({days: 3}).toISO();

return [{
  json: {
    ...items[0].json,
    followUpDate,
    followUpType: 'check-in',
    message: 'Schedule follow-up check-in email'
  }
}];
