parameters.functionCode:
// Process appointment booking data
const bookingData = items[0].json;

// Validate required fields
if (!bookingData.customerEmail || !bookingData.appointmentDate) {
  throw new Error('Customer email and appointment date are required');
}

// Generate booking confirmation number
const bookingId = 'APPT-' + Date.now();

// Parse appointment date
const appointmentDateTime = new Date(bookingData.appointmentDate);
const reminderDate24h = new Date(appointmentDateTime.getTime() - (24 * 60 * 60 * 1000));
const reminderDate2h = new Date(appointmentDateTime.getTime() - (2 * 60 * 60 * 1000));

const appointment = {
  bookingId,
  customerName: bookingData.customerName || 'Customer',
  customerEmail: bookingData.customerEmail,
  customerPhone: bookingData.customerPhone || '',
  service: bookingData.service || 'Consultation',
  appointmentDate: appointmentDateTime.toISOString(),
  duration: bookingData.duration || 60,
  location: bookingData.location || 'Our Office',
  notes: bookingData.notes || '',
  status: 'confirmed',
  bookedAt: new Date().toISOString(),
  reminderDate24h: reminderDate24h.toISOString(),
  reminderDate2h: reminderDate2h.toISOString(),
  provider: bookingData.provider || 'Default Provider'
};

return [{ json: appointment }];

